mapper.select:
select
_user.id, _user.user_code, _user.user_name, _user.sex,
_user.age, _user.birthday, _user.live_city, _user.check_status, _user.create_time, _user.remark
FROM api_user _user


    and _user.degree = #{degree}


    and _user.height >= #{minHeight}


     and _user.height <= #{maxHeight} 


    and _user.user_name like CONCAT('%',#{userName},'%')


    and _user.user_code like CONCAT('%',#{userCode},'%')


    and _user.phone like CONCAT('%',#{phone},'%')


    and _user.sex =#{sex}


    and _user.check_status =#{checkStatus}


    and _user.live_city like CONCAT('%',#{liveCity},'%')


     and DATE_FORMAT(_user.birthday, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{birthDayBeginTime,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')   


      and DATE_FORMAT(_user.birthday, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{birthDayEndTime,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')    


      and DATE_FORMAT(_user.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP}, '%Y-%m-%d')   


      and DATE_FORMAT(_user.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{deadline,jdbcType=TIMESTAMP}, '%Y-%m-%d')    


    and _user.marital_status =#{maritalStatus}


    AND _user.live_city in

      #{item}



order by _user.create_time desc
limit #{page}, #{pageSize}

select count(1) from (
select
_user.id, _user.user_code, _user.user_name, _user.sex,
_user.age, _user.birthday,  _user.live_city, _user.check_status, _user.create_time, _user.remark
FROM api_user _user


    and _user.degree = #{degree}


    and _user.height >= #{minHeight}


     and _user.height <= #{maxHeight} 


    and _user.user_name like CONCAT('%',#{userName},'%')


    and _user.user_code like CONCAT('%',#{userCode},'%')


    and _user.phone like CONCAT('%',#{phone},'%')


    and _user.sex =#{sex}


    and _user.check_status =#{checkStatus}


    and _user.live_city like CONCAT('%',#{liveCity},'%')


     and DATE_FORMAT(_user.birthday, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{birthDayBeginTime,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')   


      and DATE_FORMAT(_user.birthday, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{birthDayEndTime,jdbcType=TIMESTAMP}, '%Y-%m-%d %H:%i:%s')    


      and DATE_FORMAT(_user.create_time, '%Y-%m-%d') >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP}, '%Y-%m-%d')   


      and DATE_FORMAT(_user.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{deadline,jdbcType=TIMESTAMP}, '%Y-%m-%d')    


    and _user.marital_status =#{maritalStatus}


    AND _user.live_city in

      #{item}



) temp
